mapper.select:
SELECT commitId, fori_userId, fori_taskId, uploadTime, result, body
FROM Commit
WHERE commitId = #{taskId};

SELECT fileName, file
FROM Commit
WHERE commitId = #{taskId};

SELECT commitId, fori_userId, fori_taskId, uploadTime, result, body
FROM Commit
WHERE fori_userId = #{uid} and fori_taskId = #{taskId};
